

function obtenerFechaVencimiento(extra = {dias:null, horas:null, minutos:null, segundos:null}) {
  let agregar = 0;
  if (extra !== null) {
    if (extra.dias !== undefined && extra.dias !== null) agregar += extra.dias * 24 * 60 * 60 * 1000;
    if (extra.horas !== undefined && extra.horas !== null) agregar += extra.horas * 60 * 60 * 1000;
    if (extra.minutos !== undefined && extra.minutos !== null) agregar += extra.minutos * 60 * 1000;
    if (extra.segundos !== undefined && extra.segundos !== null) agregar += extra.segundos * 1000;
  }
  let date = new Date();
  date.setTime(date.getTime() + agregar);
  return date;
}

module.exports = { obtenerFechaVencimiento };